void CStartStage::PressAgainIDPart()
{
	system("cls");
	WelcomeMessage();
	printf("                                                                              Please enter a word of 10 characters or less                                                                              \n");
	printf("                                                                                    Using only alphabets and numbers                                                                                    \n");
	printf("                                                                                             ");
}

string CStartStage::GetInputID()
{
	int cnt = 0;
	string ID = "";
	std::regex e("[a-zA-Z0-9]+");

	while (1)
	{
		if (cnt > CPlayer::GetInst()->GetMaxNameLength())
		{
			PressAgainIDPart();
			cnt = 0;
		}

		char c = _getch();

		switch (c)
		{
		case 3: // exit
			return "exit";
		case 13: // press enter
			return ID;
		default:
			break;
		}

		ID.push_back(c);

		//if korean -> 2byte
		if (_kbhit()) {
			c = _getch();
			ID.push_back(c);
		}

		//no alphabets and numbers
		if(!std::regex_match(ID, e))
		{ 
			PressAgainIDPart();
			cnt = 0;
			ID = "";
			continue;
		}

		printf("%c", c);
		cnt++;
	}
	return ID;
}

