#include "pch.h"
#include "CVillageStage.h"
#include "CKeyManager.h"

void CVillageStage::StageInit()
{
}

void CVillageStage::StageTick()
{
	if (bCallRender)
	{
		StageRender();
		bCallRender = false;
	}

	if (CKeyManager::GetInst()->GetKeyState(KEY_TYPE::SPACE) == KEY_STATE::TAP)
	{
		ChangeDestination();
	}

	fAccumulatedTime += GlobalData.DeltaTime;
	if (isDestArrowHide && fAccumulatedTime > fDestArrowHideTime)
	{
		isDestArrowHide = false;
		fAccumulatedTime = 0.0f;
		bCallRender = true;
	}
	else if (!isDestArrowHide && fAccumulatedTime > fDestArrowShowTime)
	{
		isDestArrowHide = true;
		fAccumulatedTime = 0.0f;
		bCallRender = true;
	}
}

void CVillageStage::StageRender()
{
	std::cout << "\033[2J\033[H";
	if (isDestArrowHide)
	{
		BasicRender();
	}
	else
	{
		switch (CurDest_Type)
		{
		case DESTINATION_TYPE::BASIC:
			BasicRender();
			break;
		case DESTINATION_TYPE::CHURCH:
			PointToChurchRender();
			break;
		case DESTINATION_TYPE::SHOP:
			PointToShopRender();
			break;
		case DESTINATION_TYPE::DUNJEON:
			PointToDungeonRender();
			break;
		}
	}
}

void CVillageStage::ChangeDestination()
{
	CurDest_Type = (DESTINATION_TYPE)((int)CurDest_Type + 1);
	if (CurDest_Type == DESTINATION_TYPE::END)
	{
		CurDest_Type = DESTINATION_TYPE::CHURCH;
	}
	bCallRender = true;
}

